const supabaseUrl="YOUR_SUPABASE_URL";const supabaseKey="YOUR_SUPABASE_ANON_KEY";const supabase=createClient(supabaseUrl,supabaseKey);document.getElementById("signupForm").addEventListener("submit",(async e=>{e.preventDefault();const email=document.getElementById("email").value;const password=document.getElementById("password").value;try{const{data:data,error:error}=await supabase.auth.signUp({email:email,password:password});if(error)throw error;document.getElementById("message").textContent="Signup successful!"}catch(error){document.getElementById("message").textContent=error.message}}));